package vetor;

public class Vetores {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n []= {3, 4, 8, 9, 6, 2};
		System.out.println("O número de casas é " + n.length);
		for (int c=0; c<=5; c++){
			System.out.println("Na posição " + c + " temos o valor " + n[c]);
		}
	}
}
